select pilot_name, rank, flights_hours,mission_type
from pilots join squadrons using (squadron_id)
order by flight_hours desc

select pilot_name, rank, squadron_name
from pilots join squadrons using (squadron_id)
where rank in ('רב טוראי', 'סמל', 'סמ"ר', 'סג"מ', 'סגן', 'סרן', 'סא"ל' )

select first_name, last_name, count(mission_id)
from pilots as p left join missions as m
using (pilot_id)
group by pilot_id
having count(mission_id)>=2;

select aircraft_id, squadron_name
from aircract as a left join squadrons as s
using (squadron_id);

select fisrt_name, last_name
from (pilots as p inner join squadrons as s using (squadron_id)) lef join airbases as a using (base_id)
where salary>17000 and base_code ="RMD";

select fisrt_name, last_name, rank, flight_houres, specialization
from pilots
order by flight_houres desc;

select first_name, last_name,squadron_name
from pilots as p left join squadrons as s
using (squardron_id);

select squadron_name
from pilots as p right join squadrons as s 
using(squadron_id)
group by squadron_id
having avg(salary)>(select avg(salary)
                    from pilots);
